sp_widget.client_script:
function($window,$scope,$timeout,$location) {
  /* widget controller */
  var c = this;

	c.olShow = false;
	c.sptGrpFaces = false;
  c.grpChat = c.data.groupchat;
	c.facefocus = 0;
	c.ignoreface = false;
	var tel = false;

	if (/Android|webOS|iPhone|iPad|iPod|BlackBerry|BB|PlayBook|IEMobile|Windows Phone|Kindle|Silk|Opera Mini/i.test(navigator.userAgent)) {
		tel = true;    
	}

	c.toggleFaces = function(event) {
		event.stopPropagation();
		c.sptGrpFaces = !c.sptGrpFaces;
	}
	c.clickFace = function(event) {
		event.stopPropagation();
		//FF Hack
		if(event.target){	c.facefocus = event.target.id
		}else{ c.facefocus = event.toElement.id }
		c.toggleShow(c.facefocus,true);
  };
	c.clickPhone = function(event) {
		event.stopPropagation();
		var ph ='';

		//FF Hack
		if(event.target){ ph = angular.element(document.getElementById(event.target.id))[0];
		}else{ ph = angular.element(document.getElementById(event.toElement.id))[0]; }
		if(tel){
			$window.open("tel:+1" + c.formatPhone(ph.dataset.phone),"_self");
		}else{
			$window.open("callto:+1" + c.formatPhone(ph.dataset.phone),"_self");
		}
		c.toggleShow(c.facefocus,false);
	};
	c.openGrpChat = function(event) {
		event.stopPropagation();
		$window.open("$c.do#", "_self");
		c.toggleShow(c.facefocus,false);
	};
	c.openChat = function(event) {
		event.stopPropagation();
		$window.open("$c.do#/with/" + c.facefocus, "_self");
		c.toggleShow(c.facefocus,false);
	};	
  c.clickClose = function(event) {
		event.stopPropagation();
		c.toggleShow(c.facefocus,false);
		c.facefocus = "";
	};

	c.toggleShow = function(elID,show) {
		var f = angular.element(document.getElementById(elID));
		var phn = angular.element(document.getElementById(elID+"phn"));
		var msg = angular.element(document.getElementById(elID+"msg"));
		var cls = angular.element(document.getElementById(elID+"cls"));
		if(show){
			f.addClass('avatar-selected');
			msg.removeClass('cbw-hidden').addClass('cbw-msg').addClass('cbw-ontop').addClass('animated').addClass('rotateInUpRight');
			phn.removeClass('cbw-hidden').addClass('cbw-phone').addClass('cbw-ontop').addClass('animated').addClass('rotateInUpRight');
			cls.removeClass('cbw-hidden').addClass('cbw-close').addClass('cbw-ontop').addClass('animated').addClass('rotateInUpRight');
		}else{
			f.removeClass('avatar-selected');
			phn.removeClass('animated').removeClass('rotateInUpRight').removeClass('cbw-ontop').removeClass('cbw-msg').addClass('cbw-hidden');
			msg.removeClass('animated').removeClass('rotateInUpRight').removeClass('cbw-ontop').removeClass('cbw-phone').addClass('cbw-hidden');
			cls.removeClass('animated').removeClass('rotateInUpRight').removeClass('cbw-ontop').removeClass('cbw-close').addClass('cbw-hidden');
		}
		c.olShow = show;
	};	

	c.formatPhone = function(s) {
    var s2 = (""+s).replace(/\D/g, '');
    var m = s2.match(/^(\d{3})(\d{3})(\d{4})$/);
    return (!m) ? null : "" + m[1] + m[2] + m[3];
  };

	//for each element that is classed as 'pull-down', set its margin-top to the difference between its own height and the height of its parent
	$('.pull-down').each(function() {
		var $this=$(this);
		console.log($this.height())
		$this.css('margin-top', $this.parent().height()-$this.height())
	});
}

