sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.users = [];
	data.activeUsers = false;

	if(options.groupchat=="true"){
		data.limit = 3;
		data.groupchat = true;
	}else{
		data.groupchat = false;
		data.limit = 4;
	}

	var usgr = new GlideRecord('sys_user_group');    
	usgr.addQuery('name', options.sptgrpname);    
	usgr.query();

  if (usgr.next()){
    var grmem = new GlideRecord('sys_user_grmember');    
		grmem.addQuery('group', usgr.sys_id);    
		grmem.query();
		var x = 0;
		while (grmem.next()){ 
			var usr = new GlideRecord('sys_user');    
		  usr.addQuery('sys_id',grmem.user.toString());    
			usr.query();
			if(usr.next()){
				if(usr.active){
					var user = {
						'id': grmem.user.toString(),
						'name': usr.user_name.getDisplayValue(),
						'lname' : usr.last_name.getDisplayValue(),
						'fname' : usr.first_name.getDisplayValue(),
						'title' : usr.title.getDisplayValue(),
						'avatar': usr.photo.getDisplayValue(),
						'bphone': usr.phone.getDisplayValue(),
						'mphone': usr.mobile_phone.getDisplayValue(),
						'present' : null
					}
					data.users.push(user);
					x++;
				}
			}
		}
		data.users = shuffle(data.users,data.limit);
		data.activeUsers = (data.users.length > 0);
	}

	//https://bost.ocks.org/mike/shuffle/
	function shuffle(array,limit) {
    var m = array.length, t, i;
		var rtn = [limit];
    // While there remain elements to shuffle
    while (m) {
      // Pick a remaining element
      i = Math.floor(Math.random() * m--);
      // And swap it with the current element.
      t = array[m];
      array[m] = array[i];
      array[i] = t;
    }
		for(var x=0;x<limit;x++){
      rtn[x] = array[x];
		}
    return rtn;
  }
})();
